public class TownMain2 {
	public Town towns[] = new Town[3];

	TownMain2(){
		this.towns[0] = new Town (20.0, 53.7, "hakodate");
		this.towns[1] = new Town (200.0, 52.9, "sapporo");
		this.towns[2] = new Town (140.0, 51.0, "kyoto");
	}	
	
	public String show () {
        return (towns[0].disp() + ", " + towns[1].disp() + ", " + towns[2].disp());
	}


	public static void main(String[] args) {
	// TODO Auto-generated method stub
	TownMain2 a = new TownMain2();
	System.out.println(a.show());
	}
}